138‚Üíexport async function createTask(data: Omit<Task, 'id' | 'createdAt' | 'updatedAt' | 'subTasks' | 'dailyUpdates'>): Promise<Task> {
139‚Üí  await delay();
140‚Üí  if (data.status === 'Blocked' && !data.blockingReason.trim()) {
141‚Üí    throw new Error('Blocking reason is required for blocked tasks.');
142‚Üí  }
143‚Üí  const now = new Date().toISOString();
144‚Üí  const task: Task = { ...data, id: generateId(), subTasks: [], dailyUpdates: [], createdAt: now, updatedAt: now };
145‚Üí  const tasks = getTasks();
146‚Üí  tasks.push(task);
147‚Üí  saveTasks(tasks);
148‚Üí  return task;
