151‚Üíexport async function updateTask(id: string, data: Partial<Omit<Task, 'id' | 'createdAt' | 'subTasks' | 'dailyUpdates'>>): Promise<Task> {
152‚Üí  await delay();
153‚Üí  const tasks = getTasks();
154‚Üí  const idx = tasks.findIndex(t => t.id === id);
155‚Üí  if (idx === -1) throw new Error('Task not found');
156‚Üí  const merged = { ...tasks[idx], ...data, updatedAt: new Date().toISOString() };
157‚Üí  if (merged.status === 'Blocked' && !merged.blockingReason.trim()) {
158‚Üí    throw new Error('Blocking reason is required for blocked tasks.');
159‚Üí  }
160‚Üí  if (merged.status !== 'Blocked') merged.blockingReason = '';
161‚Üí  tasks[idx] = merged;
162‚Üí  saveTasks(tasks);
163‚Üí  return merged;
164‚Üí}
