211‚Üí  const tasks = getTasks();
212‚Üí  const task = tasks.find(t => t.id === taskId);
213‚Üí  if (!task) throw new Error('Task not found');
214‚Üí  const members = getMembers();
215‚Üí  const author = members.find(m => m.id === authorId);
216‚Üí  if (!author) throw new Error('Author not found');
217‚Üí  const now = new Date().toISOString();
218‚Üí  const update: DailyUpdate = { id: generateId(), taskId, authorId, authorName: author.name, content, createdAt: now, updatedAt: now, edited: false };
219‚Üí  task.dailyUpdates.unshift(update);
220‚Üí  task.updatedAt = now;
221‚Üí  saveTasks(tasks);
222‚Üí  return update;
223‚Üí}
