225‚Üíexport async function editDailyUpdate(taskId: string, updateId: string, content: string): Promise<void> {
226‚Üí  await delay();
227‚Üí  const tasks = getTasks();
228‚Üí  const task = tasks.find(t => t.id === taskId);
229‚Üí  if (!task) throw new Error('Task not found');
230‚Üí  const upd = task.dailyUpdates.find(u => u.id === updateId);
231‚Üí  if (!upd) throw new Error('Update not found');
232‚Üí  const age = Date.now() - new Date(upd.createdAt).getTime();
233‚Üí  if (age > 24 * 3600000) throw new Error('Updates can only be edited within 24 hours.');
234‚Üí  upd.content = content;
235‚Üí  upd.updatedAt = new Date().toISOString();
236‚Üí  upd.edited = true;
237‚Üí  saveTasks(tasks);
238‚Üí}
