246‚Üí  if (!upd) throw new Error('Update not found');
247‚Üí  const age = Date.now() - new Date(upd.createdAt).getTime();
248‚Üí  if (age > 24 * 3600000) throw new Error('Updates can only be deleted within 24 hours.');
249‚Üí  task.dailyUpdates = task.dailyUpdates.filter(u => u.id !== updateId);
250‚Üí  saveTasks(tasks);
