 3‚Üíexport function formatRelativeDate(dateStr: string): string {
 4‚Üí  const date = new Date(dateStr);
 5‚Üí  const days = differenceInDays(new Date(), date);
 6‚Üí  if (days < 7) {
 7‚Üí    return formatDistanceToNow(date, { addSuffix: true });
 8‚Üí  }
 9‚Üí  return format(date, 'MMM d');
10‚Üí}
