 3‚Üíconst MOBILE_BREAKPOINT = 768;
 4‚Üí
 5‚Üíexport function useIsMobile() {
 6‚Üí  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined);
 7‚Üí
 8‚Üí  React.useEffect(() => {
 9‚Üí    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`);
10‚Üí    const onChange = () => {
11‚Üí      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
12‚Üí    };
13‚Üí    mql.addEventListener("change", onChange);
14‚Üí    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT);
15‚Üí    return () => mql.removeEventListener("change", onChange);
16‚Üí  }, []);
17‚Üí
18‚Üí  return !!isMobile;
19‚Üí}
