  1‚Üíimport * as React from "react";
  2‚Üí
  3‚Üíimport type { ToastActionElement, ToastProps } from "@/components/ui/toast";
  4‚Üí
  5‚Üíconst TOAST_LIMIT = 1;
  6‚Üíconst TOAST_REMOVE_DELAY = 1000000;
  7‚Üí
  8‚Üítype ToasterToast = ToastProps & {
  9‚Üí  id: string;
 10‚Üí  title?: React.ReactNode;
 11‚Üí  description?: React.ReactNode;
 12‚Üí  action?: ToastActionElement;
 13‚Üí};
 14‚Üí
 15‚Üíconst actionTypes = {
 16‚Üí  ADD_TOAST: "ADD_TOAST",
 17‚Üí  UPDATE_TOAST: "UPDATE_TOAST",
 18‚Üí  DISMISS_TOAST: "DISMISS_TOAST",
 19‚Üí  REMOVE_TOAST: "REMOVE_TOAST",
 20‚Üí} as const;
 21‚Üí
 22‚Üílet count = 0;
 23‚Üí
 24‚Üífunction genId() {
 25‚Üí  count = (count + 1) % Number.MAX_SAFE_INTEGER;
 26‚Üí  return count.toString();
 27‚Üí}
 28‚Üí
 29‚Üítype ActionType = typeof actionTypes;
 30‚Üí
 31‚Üítype Action =
 32‚Üí  | {
 33‚Üí      type: ActionType["ADD_TOAST"];
 34‚Üí      toast: ToasterToast;
 35‚Üí    }
 36‚Üí  | {
 37‚Üí      type: ActionType["UPDATE_TOAST"];
 38‚Üí      toast: Partial<ToasterToast>;
 39‚Üí    }
 40‚Üí  | {
 41‚Üí      type: ActionType["DISMISS_TOAST"];
 42‚Üí      toastId?: ToasterToast["id"];
 43‚Üí    }
 44‚Üí  | {
 45‚Üí      type: ActionType["REMOVE_TOAST"];
 46‚Üí      toastId?: ToasterToast["id"];
 47‚Üí    };
 48‚Üí
 49‚Üíinterface State {
 50‚Üí  toasts: ToasterToast[];
 51‚Üí}
 52‚Üí
 53‚Üíconst toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>();
 54‚Üí
 55‚Üíconst addToRemoveQueue = (toastId: string) => {
 56‚Üí  if (toastTimeouts.has(toastId)) {
 57‚Üí    return;
 58‚Üí  }
 59‚Üí
 60‚Üí  const timeout = setTimeout(() => {
 61‚Üí    toastTimeouts.delete(toastId);
 62‚Üí    dispatch({
 63‚Üí      type: "REMOVE_TOAST",
 64‚Üí      toastId: toastId,
 65‚Üí    });
 66‚Üí  }, TOAST_REMOVE_DELAY);
 67‚Üí
 68‚Üí  toastTimeouts.set(toastId, timeout);
 69‚Üí};
 70‚Üí
 71‚Üíexport const reducer = (state: State, action: Action): State => {
 72‚Üí  switch (action.type) {
 73‚Üí    case "ADD_TOAST":
 74‚Üí      return {
 75‚Üí        ...state,
 76‚Üí        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
 77‚Üí      };
 78‚Üí
 79‚Üí    case "UPDATE_TOAST":
 80‚Üí      return {
 81‚Üí        ...state,
 82‚Üí        toasts: state.toasts.map((t) => (t.id === action.toast.id ? { ...t, ...action.toast } : t)),
 83‚Üí      };
 84‚Üí
 85‚Üí    case "DISMISS_TOAST": {
 86‚Üí      const { toastId } = action;
 87‚Üí
 88‚Üí      // ! Side effects ! - This could be extracted into a dismissToast() action,
 89‚Üí      // but I'll keep it here for simplicity
 90‚Üí      if (toastId) {
 91‚Üí        addToRemoveQueue(toastId);
 92‚Üí      } else {
 93‚Üí        state.toasts.forEach((toast) => {
 94‚Üí          addToRemoveQueue(toast.id);
 95‚Üí        });
 96‚Üí      }
 97‚Üí
 98‚Üí      return {
 99‚Üí        ...state,
100‚Üí        toasts: state.toasts.map((t) =>
101‚Üí          t.id === toastId || toastId === undefined
102‚Üí            ? {
103‚Üí                ...t,
104‚Üí                open: false,
105‚Üí              }
106‚Üí            : t,
107‚Üí        ),
108‚Üí      };
109‚Üí    }
110‚Üí    case "REMOVE_TOAST":
111‚Üí      if (action.toastId === undefined) {
112‚Üí        return {
113‚Üí          ...state,
114‚Üí          toasts: [],
115‚Üí        };
116‚Üí      }
117‚Üí      return {
118‚Üí        ...state,
119‚Üí        toasts: state.toasts.filter((t) => t.id !== action.toastId),
120‚Üí      };
121‚Üí  }
122‚Üí};
123‚Üí
124‚Üíconst listeners: Array<(state: State) => void> = [];
125‚Üí
126‚Üílet memoryState: State = { toasts: [] };
127‚Üí
128‚Üífunction dispatch(action: Action) {
129‚Üí  memoryState = reducer(memoryState, action);
130‚Üí  listeners.forEach((listener) => {
131‚Üí    listener(memoryState);
132‚Üí  });
133‚Üí}
134‚Üí
135‚Üítype Toast = Omit<ToasterToast, "id">;
136‚Üí
137‚Üífunction toast({ ...props }: Toast) {
138‚Üí  const id = genId();
139‚Üí
140‚Üí  const update = (props: ToasterToast) =>
141‚Üí    dispatch({
142‚Üí      type: "UPDATE_TOAST",
143‚Üí      toast: { ...props, id },
144‚Üí    });
145‚Üí  const dismiss = () => dispatch({ type: "DISMISS_TOAST", toastId: id });
146‚Üí
147‚Üí  dispatch({
148‚Üí    type: "ADD_TOAST",
149‚Üí    toast: {
150‚Üí      ...props,
151‚Üí      id,
152‚Üí      open: true,
153‚Üí      onOpenChange: (open) => {
154‚Üí        if (!open) dismiss();
155‚Üí      },
156‚Üí    },
157‚Üí  });
158‚Üí
159‚Üí  return {
160‚Üí    id: id,
161‚Üí    dismiss,
162‚Üí    update,
163‚Üí  };
164‚Üí}
165‚Üí
166‚Üífunction useToast() {
167‚Üí  const [state, setState] = React.useState<State>(memoryState);
168‚Üí
169‚Üí  React.useEffect(() => {
170‚Üí    listeners.push(setState);
171‚Üí    return () => {
172‚Üí      const index = listeners.indexOf(setState);
173‚Üí      if (index > -1) {
174‚Üí        listeners.splice(index, 1);
175‚Üí      }
176‚Üí    };
177‚Üí  }, [state]);
178‚Üí
179‚Üí  return {
180‚Üí    ...state,
181‚Üí    toast,
182‚Üí    dismiss: (toastId?: string) => dispatch({ type: "DISMISS_TOAST", toastId }),
183‚Üí  };
184‚Üí}
185‚Üí
186‚Üíexport { useToast, toast };
