python3 -c "
import json

with open('/home/parth/.REDACTED.jsonl', 'r') as f:
    for line_num, line in enumerate(f, 1):
        try:
            obj = json.loads(line.strip())
            # Check if it's an assistant message
            if obj.get('type') == 'assistant':
                msg = obj.get('message', {})
                content_list = msg.get('content', [])
                for block in content_list:
                    if block.get('type') == 'text':
                        text = block.get('text', '')
                        if '## User Journey Flows' in text or 'User Journey Flows' in text:
                            print(f'=== Found in line {line_num} ===')
                            print(text)
                            print('=== END ===')
                            print()
        except json.JSONDecodeError:
            pass
" 2>&1

python3 -c "
import json
with open('/home/parth/.REDACTED.jsonl') as f:
    for i, line in enumerate(f):
        try:
            obj = json.loads(line)
            if obj.get('type') == 'assistant':
                msg = obj.get('message', {})
                content = msg.get('content', [])
                for block in content:
                    if block.get('type') == 'text':
                        text = block.get('text', '')
                        if '## User Journey Flows' in text and 'Flow Optimization Principles' in text:
                            # Find the start of the section
                            idx = text.find('## User Journey Flows')
                            print(text[idx:])
                            exit()
        except:
            pass
