from __future__ import annotations

from datetime import datetime, timezone
import re
import uuid

import sqlalchemy as sa
from sqlalchemy import func, or_, select
from sqlalchemy.orm import Session

from app.models.member import Member
from app.models.task import Task
from app.schemas.task import TaskCreate, TaskStatus, TaskUpdate

_GEAR_ID_PATTERN = re.compile(r"^\d{4}$")
_ASSIGNEE_FILTER_ERROR = "Assignee filter must be a UUID or 'unassigned'"


def _validate_gear_id(gear_id: str | None) -> None:
    if gear_id is None:
        return
    if not _GEAR_ID_PATTERN.fullmatch(gear_id):
        raise ValueError("GEAR ID must be exactly 4 digits")


def _validate_blocking_reason(status: TaskStatus, blocking_reason: str | None) -> None:
    if status != TaskStatus.BLOCKED:
        return
    if not (blocking_reason or "").strip():
        raise ValueError("Blocking reason is required when status is Blocked")


def _resolve_assignee_name(db: Session, assignee_id: uuid.UUID) -> str:
    statement = select(Member.name).where(Member.id == assignee_id)
    assignee_name = db.execute(statement).scalar_one_or_none()
    if assignee_name is None:
        raise ValueError("Assignee not found")
    return assignee_name


def list_tasks(
    db: Session,
    *,
    status: TaskStatus | None = None,
    priority: str | None = None,
    assignee: str | None = None,
    search: str | None = None,
    sort: str = "updated",
) -> list[Task]:
    statement = select(Task)

    if status is not None:
        statement = statement.where(Task.status == status.value)

    if priority is not None:
        statement = statement.where(Task.priority == priority)

    if assignee is not None:
        if assignee == "unassigned":
            statement = statement.where(Task.assignee_id.is_(None))
        else:
            try:
                assignee_id = uuid.UUID(assignee)
            except ValueError as exc:
                raise ValueError(_ASSIGNEE_FILTER_ERROR) from exc
            statement = statement.where(Task.assignee_id == assignee_id)

    if search:
        search_term = f"%{search.strip()}%"
        statement = statement.where(
            or_(
                Task.title.ilike(search_term),
                Task.description.ilike(search_term),
                Task.gear_id.ilike(search_term),
            )
        )

    if sort == "priority":
        priority_order = sa.case(
            (Task.priority == "High", 1),
            (Task.priority == "Medium", 2),
            (Task.priority == "Low", 3),
            else_=99,
        )
        statement = statement.order_by(priority_order.asc(), Task.updated_at.desc())
    elif sort == "status":
        status_order = sa.case(
            (Task.status == "To Do", 1),
            (Task.status == "In Progress", 2),
            (Task.status == "Blocked", 3),
            (Task.status == "Done", 4),
            else_=99,
        )
        statement = statement.order_by(status_order.asc(), Task.updated_at.desc())
    else:
        statement = statement.order_by(Task.updated_at.desc())

    return list(db.scalars(statement))


def get_task_by_id(db: Session, task_id: uuid.UUID) -> Task | None:
    statement = select(Task).where(Task.id == task_id)
    return db.scalars(statement).first()


def create_task(db: Session, payload: TaskCreate) -> Task:
    blocking_reason = (payload.blocking_reason or "").strip()
    if payload.status != TaskStatus.BLOCKED:
        blocking_reason = ""

    assignee_name: str | None = None
    if payload.assignee_id is not None:
        assignee_name = _resolve_assignee_name(db, payload.assignee_id)

    task = Task(
        title=payload.title,
        description=payload.description,
        status=payload.status.value,
        priority=payload.priority.value,
        assignee_id=payload.assignee_id,
        assignee_name=assignee_name,
        gear_id=payload.gear_id,
        blocking_reason=blocking_reason,
    )
    db.add(task)
    db.commit()
    db.refresh(task)
    return task


def update_task(db: Session, task: Task, payload: TaskUpdate) -> Task:
    update_data = payload.model_dump(exclude_unset=True)
    if not update_data:
        return task

    if "gear_id" in update_data:
        _validate_gear_id(update_data["gear_id"])

    next_status_raw = update_data.get("status", task.status)
    next_status = TaskStatus(next_status_raw)

    if "blocking_reason" in update_data and update_data["blocking_reason"] is None:
        update_data["blocking_reason"] = ""

    blocking_reason_raw = update_data.get("blocking_reason", task.blocking_reason)
    blocking_reason = (blocking_reason_raw or "").strip()
    _validate_blocking_reason(next_status, blocking_reason)

    if next_status != TaskStatus.BLOCKED and (
        ("status" in update_data and update_data["status"] != TaskStatus.BLOCKED)
        or task.status == TaskStatus.BLOCKED.value
    ):
        update_data["blocking_reason"] = ""
    elif "blocking_reason" in update_data:
        update_data["blocking_reason"] = blocking_reason

    if "assignee_id" in update_data:
        assignee_id = update_data["assignee_id"]
        if assignee_id is None:
            update_data["assignee_name"] = None
        else:
            update_data["assignee_name"] = _resolve_assignee_name(db, assignee_id)

    for field, value in update_data.items():
        if field == "status" and isinstance(value, TaskStatus):
            setattr(task, field, value.value)
            continue
        if field == "priority" and value is not None:
            setattr(task, field, value.value if hasattr(value, "value") else value)
            continue
        setattr(task, field, value)

    task.updated_at = datetime.now(timezone.utc)
    db.add(task)
    db.commit()
    db.refresh(task)
    return task


def delete_task(db: Session, task: Task) -> None:
    db.delete(task)
    db.commit()

from __future__ import annotations

import re
import uuid
from typing import Literal

from fastapi import APIRouter, Depends, HTTPException, Query, status
from sqlalchemy.exc import OperationalError
from sqlalchemy.orm import Session

from app.api.deps import get_db
from app.crud import task as task_crud
from app.models.task import Task
from app.schemas.task import TaskCreate, TaskPriority, TaskResponse, TaskStatus, TaskUpdate

router = APIRouter()

_GEAR_ID_PATTERN = re.compile(r"^\d{4}$")
_GEAR_ID_ERROR = "GEAR ID must be exactly 4 digits"
_BLOCKED_REASON_ERROR = "Blocking reason is required when status is Blocked"
_TRANSIENT_DB_ERROR = "Database connection lost. Please retry."


def _validate_gear_id(gear_id: str | None) -> None:
    if gear_id is None:
        return
    if not _GEAR_ID_PATTERN.fullmatch(gear_id):
        raise HTTPException(status_code=status.HTTP_400_BAD_REQUEST, detail=_GEAR_ID_ERROR)


def _validate_blocked_status(status_value: str, blocking_reason: str | None) -> None:
    if status_value != TaskStatus.BLOCKED.value:
        return
    if not (blocking_reason or "").strip():
        raise HTTPException(status_code=status.HTTP_400_BAD_REQUEST, detail=_BLOCKED_REASON_ERROR)


def _handle_operational_error(db: Session, exc: OperationalError) -> None:
    db.rollback()
    raise HTTPException(status_code=status.HTTP_503_SERVICE_UNAVAILABLE, detail=_TRANSIENT_DB_ERROR) from exc


@router.get("", response_model=list[TaskResponse])
def list_tasks(
    status_filter: TaskStatus | None = Query(default=None, alias="status"),
    priority_filter: TaskPriority | None = Query(default=None, alias="priority"),
    assignee: str | None = Query(default=None),
    search: str | None = Query(default=None),
    sort: Literal["updated", "priority", "status"] = Query(default="updated"),
    db: Session = Depends(get_db),
) -> list[Task]:
    try:
        return task_crud.list_tasks(
            db,
            status=status_filter,
            priority=priority_filter.value if priority_filter else None,
            assignee=assignee,
            search=search,
            sort=sort,
        )
    except ValueError as exc:
        raise HTTPException(status_code=status.HTTP_400_BAD_REQUEST, detail=str(exc)) from exc
    except OperationalError as exc:
        _handle_operational_error(db, exc)


@router.get("/{task_id}", response_model=TaskResponse)
def get_task(task_id: uuid.UUID, db: Session = Depends(get_db)) -> Task:
    try:
        task = task_crud.get_task_by_id(db, task_id)
    except OperationalError as exc:
        _handle_operational_error(db, exc)

    if not task:
        raise HTTPException(status_code=status.HTTP_404_NOT_FOUND, detail="Task not found")
    return task


@router.post("", response_model=TaskResponse, status_code=status.HTTP_201_CREATED)
def create_task(payload: TaskCreate, db: Session = Depends(get_db)) -> Task:
    _validate_gear_id(payload.gear_id)
    _validate_blocked_status(payload.status.value, payload.blocking_reason)

    try:
        return task_crud.create_task(db, payload)
    except ValueError as exc:
        db.rollback()
        raise HTTPException(status_code=status.HTTP_400_BAD_REQUEST, detail=str(exc)) from exc
    except OperationalError as exc:
        _handle_operational_error(db, exc)


@router.patch("/{task_id}", response_model=TaskResponse)
def update_task(task_id: uuid.UUID, payload: TaskUpdate, db: Session = Depends(get_db)) -> Task:
    _validate_gear_id(payload.gear_id)

    try:
        task = task_crud.get_task_by_id(db, task_id)
    except OperationalError as exc:
        _handle_operational_error(db, exc)

    if not task:
        raise HTTPException(status_code=status.HTTP_404_NOT_FOUND, detail="Task not found")

    resolved_status = payload.status.value if payload.status is not None else task.status
    resolved_blocking_reason = payload.blocking_reason if "blocking_reason" in payload.model_fields_set else task.blocking_reason
    _validate_blocked_status(resolved_status, resolved_blocking_reason)

    try:
        return task_crud.update_task(db, task, payload)
    except ValueError as exc:
        db.rollback()
        raise HTTPException(status_code=status.HTTP_400_BAD_REQUEST, detail=str(exc)) from exc
    except OperationalError as exc:
        _handle_operational_error(db, exc)


@router.delete("/{task_id}", status_code=status.HTTP_204_NO_CONTENT)
def delete_task(task_id: uuid.UUID, db: Session = Depends(get_db)) -> None:
    try:
        task = task_crud.get_task_by_id(db, task_id)
    except OperationalError as exc:
        _handle_operational_error(db, exc)

    if not task:
        raise HTTPException(status_code=status.HTTP_404_NOT_FOUND, detail="Task not found")

    try:
        task_crud.delete_task(db, task)
    except OperationalError as exc:
        _handle_operational_error(db, exc)

