import { test, expect } from '../support/merged-fixtures';

test.describe('Settings Smoke', () => {
  test('should navigate to settings and verify connection form', async ({ page }) => {
    await test.step('Navigating to settings via URL', async () => {
      await page.goto('/settings');
    });

    await test.step('Verifying settings form header', async () => {
      await expect(page.getByRole('heading', { name: 'Settings' })).toBeVisible();
    });

    await test.step('Verifying connection fields exist', async () => {
      await expect(page.getByLabel('Host')).toBeVisible();
      await expect(page.getByLabel('Port')).toBeVisible();
      await expect(page.getByLabel('Database Name')).toBeVisible();
      await expect(page.getByLabel('Username')).toBeVisible();
      await expect(page.getByLabel('Password', { exact: true })).toBeVisible();
    });

    await test.step('Verifying version footer', async () => {
      await expect(page.getByText('v1.0.0')).toBeVisible();
    });
  });
});

