  ];

  const now = new Date();
  const h = (hoursAgo: number) => new Date(now.getTime() - hoursAgo * ONE_HOUR_MS).toISOString();

  const tasks: Task[] = [
    {
      id: 't1', title: 'Set up CI/CD pipeline', description: 'Configure GitHub Actions for automated testing and deployment to staging.',
