import { defineConfig, devices } from '@playwright/test';
import path from 'path';
import { fileURLToPath } from 'url';
import { config as dotenvConfig } from 'dotenv';

const __filename = fileURLToPath(import.meta.url);
const __dirname = path.dirname(__filename);

// Load .env from project root
dotenvConfig({
  path: path.resolve(__dirname, '../../.env'),
});

// Use 'local' as default environment if not specified
const environment = process.env.TEST_ENV || 'local';

// Load environment-specific config
const envConfigMap = {
  local: {
    baseURL: 'http://localhost:8080', // Vite configured port
  },
  staging: {
    baseURL: 'https://staging.taskflow.example.com',
  },
  production: {
    baseURL: 'https://taskflow.example.com',
  },
};

const activeEnvConfig = envConfigMap[environment as keyof typeof envConfigMap] || envConfigMap.local;

console.log(`✅ Running Playwright tests against: ${environment.toUpperCase()} (${activeEnvConfig.baseURL})`);

export default defineConfig({
  testDir: path.resolve(__dirname, './tests/e2e/specs'),
  outputDir: path.resolve(__dirname, './test-results'),
  fullyParallel: true,
  forbidOnly: !!process.env.CI,
  retries: process.env.CI ? 2 : 0,
  workers: process.env.CI ? 1 : undefined,
  reporter: [
    ['html', { outputFolder: 'playwright-report', open: 'never' }],
    ['list'],
  ],
  use: {
    baseURL: activeEnvConfig.baseURL,
    actionTimeout: 15000,
    navigationTimeout: 30000,
    trace: 'on-first-retry',
    screenshot: 'only-on-failure',
    video: 'retain-on-failure',
    headless: true,
  },
  timeout: 60000,
  expect: { timeout: 10000 },
  projects: [
    {
      name: 'chromium',
      use: { ...devices['Desktop Chrome'] },
    },
    // Uncomment to test other browsers
    // {
    //   name: 'firefox',
    //   use: { ...devices['Desktop Firefox'] },
    // },
    // {
    //   name: 'webkit',
    //   use: { ...devices['Desktop Safari'] },
    // },
  ],
  // Run Vite dev server locally if not in CI
  webServer: !process.env.CI && environment === 'local' ? {
    command: 'npm run dev',
    url: 'http://localhost:8080',
    reuseExistingServer: true,
    timeout: 120000,
  } : undefined,
});

