 * Runs in mock-adapter mode (no backend required).
 */

test.describe('Task List — Dashboard', () => {
  test('should show seeded tasks on dashboard load', async ({ page }) => {
    await page.goto('/');

    await test.step('Verify dashboard heading', async () => {
      await expect(page.getByRole('heading', { name: 'Dashboard' })).toBeVisible();
    });

    await test.step('Verify New Task button is present', async () => {
      await expect(page.getByRole('button', { name: 'New Task' })).toBeVisible();
    });

    await test.step('Verify at least one task card is visible', async () => {
      // Seeded mock has 6 tasks; expect at least one card
      const taskCards = page.locator('[data-testid="task-card"], .task-card, [class*="card"]');
      // Fall back to checking task title text from seed data
      await expect(page.getByText('Set up CI/CD pipeline')).toBeVisible();
    });
  });

  test('should display task titles from seed data', async ({ page }) => {
    await page.goto('/');

    await test.step('Check seeded task titles visible', async () => {
      await expect(page.getByText('Set up CI/CD pipeline')).toBeVisible();
      await expect(page.getByText('Database migration stuck')).toBeVisible();
    });
  });
});

test.describe('New Task Dialog', () => {
  test('should open task creation dialog on New Task click', async ({ page }) => {
    await page.goto('/');

    await test.step('Click New Task', async () => {
      await page.getByRole('button', { name: 'New Task' }).click();
    });

    await test.step('Dialog should appear with a Title field', async () => {
      await expect(page.getByRole('dialog')).toBeVisible();
      await expect(page.getByLabel('Title')).toBeVisible();
    });
  });

  test('should close dialog on Cancel or Escape', async ({ page }) => {
    await page.goto('/');
    await page.getByRole('button', { name: 'New Task' }).click();
    await expect(page.getByRole('dialog')).toBeVisible();

    await test.step('Close with Escape key', async () => {
      await page.keyboard.press('Escape');
      await expect(page.getByRole('dialog')).not.toBeVisible();
    });
  });

  test('should require title before submission', async ({ page }) => {
    await page.goto('/');
    await page.getByRole('button', { name: 'New Task' }).click();
    await expect(page.getByRole('dialog')).toBeVisible();

    await test.step('Submit with empty title', async () => {
      // Clear title if pre-filled and try to submit
