    13â†’const PRIORITY_ORDER: Record<Task['priority'], number> = { High: 0, Medium: 1, Low: 2 };
    14â†’const STATUS_ORDER: Record<Task['status'], number> = { 'To Do': 1, 'In Progress': 2, Blocked: 3, Done: 4 };
    15â†’
    16â†’function byUpdatedAtDesc(a: Task, b: Task): number {
    17â†’  return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
    18â†’}
    19â†’
    20â†’export function filterAndSortDashboardTasks(tasks: Task[], query: DashboardQuery): Task[] {
    21â†’  const { statusFilter, priorityFilter, assigneeFilter, search, sort } = query;
    22â†’  let list = [...tasks];
    23â†’
    24â†’  if (statusFilter !== 'all') list = list.filter(t => t.status === statusFilter);
    25â†’  if (priorityFilter !== 'all') list = list.filter(t => t.priority === priorityFilter);
    26â†’  if (assigneeFilter === 'unassigned') list = list.filter(t => !t.assigneeId);
    27â†’  else if (assigneeFilter !== 'all') list = list.filter(t => t.assigneeId === assigneeFilter);
    28â†’  if (search.trim()) {
    29â†’    const q = search.toLowerCase();
    30â†’    list = list.filter(t =>
    31â†’      t.title.toLowerCase().includes(q) ||
    32â†’      (t.description ?? '').toLowerCase().includes(q) ||
    33â†’      (t.gearId ?? '').includes(q)
    34â†’    );
    35â†’  }
    36â†’
    37â†’  if (sort === 'updated') list.sort(byUpdatedAtDesc);
    38â†’  else if (sort === 'priority') {
    39â†’    list.sort((a, b) => {
    40â†’      const delta = PRIORITY_ORDER[a.priority] - PRIORITY_ORDER[b.priority];
    41â†’      if (delta !== 0) return delta;
