import { fireEvent, render, screen, waitFor } from "@testing-library/react";
import { describe, expect, it, vi } from "vitest";
import { TaskFormDialog } from "@/components/TaskFormDialog";
import type { Task, TeamMember } from "@/types";

const members: TeamMember[] = [
  { id: "m1", name: "Alice Chen", email: "alice@example.com", active: true },
];

describe("TaskFormDialog", () => {
  it("submits gearId as null when the GEAR ID field is empty", async () => {
    const onSubmit = vi.fn().mockResolvedValue(undefined);
    const onOpenChange = vi.fn();

    render(
      <TaskFormDialog
        open
        onOpenChange={onOpenChange}
        onSubmit={onSubmit}
        members={members}
      />,
    );

    fireEvent.change(screen.getByLabelText("Title *"), {
      target: { value: "Create task without gear id" },
    });
    fireEvent.click(screen.getByRole("button", { name: "Create Task" }));

    await waitFor(() => {
      expect(onSubmit).toHaveBeenCalledWith(
        expect.objectContaining({
          title: "Create task without gear id",
          description: null,
          gearId: null,
        }),
      );
    });
  });

  it("handles null task description in edit mode without crashing", async () => {
    const onSubmit = vi.fn().mockResolvedValue(undefined);
    const onOpenChange = vi.fn();
    const task: Task = {
      id: "t-null-desc",
      title: "Task with null description",
      description: null,
      status: "To Do",
      priority: "Medium",
      assigneeId: null,
      assigneeName: null,
      gearId: null,
      blockingReason: "",
      subTasks: [],
      dailyUpdates: [],
      createdAt: "2026-02-21T00:00:00.000Z",
      updatedAt: "2026-02-21T00:00:00.000Z",
    };

    render(
      <TaskFormDialog
        open
        onOpenChange={onOpenChange}
        onSubmit={onSubmit}
        members={members}
        task={task}
      />,
    );

    fireEvent.click(screen.getByRole("button", { name: "Save Changes" }));

    await waitFor(() => {
      expect(onSubmit).toHaveBeenCalledWith(
        expect.objectContaining({
          title: "Task with null description",
          description: null,
        }),
      );
    });
  });
});

