     1→/**
     2→ * Tests for MockApiClient task CRUD, filtering, sorting, and validation.
     3→ * These tests cover existing mock adapter behaviors — they should pass now.
     4→ *
     5→ * Related: Story 2.1 (backend parity), Story 2.2 (mock adapter)
     6→ */
     7→import { beforeEach, describe, expect, it, vi } from "vitest";
     8→import type { ApiClient, CreateTaskData } from "@/lib/api/client";
     9→
    10→async function createClient(): Promise<ApiClient> {
    11→  localStorage.clear();
    12→  vi.resetModules();
    13→  const module = await import("@/lib/api/adapters/mock");
    14→  return new module.MockApiClient();
    15→}
    16→
    17→async function makeTask(
    18→  client: ApiClient,
    19→  overrides: Partial<CreateTaskData> = {},
    20→): Promise<Awaited<ReturnType<ApiClient["createTask"]>>> {
    21→  return client.createTask({
    22→    title: "Test task",
    23→    description: "",
    24→    status: "To Do",
    25→    priority: "Medium",
    26→    assigneeId: null,
    27→    assigneeName: null,
    28→    gearId: null,
    29→    blockingReason: "",
    30→    ...overrides,
    31→  });
    32→}
    33→
    34→describe("MockApiClient — Task CRUD", () => {
    35→  let client: ApiClient;
    36→
    37→  beforeEach(async () => {
    38→    client = await createClient();
    39→  });
    40→
    41→  it("creates a task and returns it with id, createdAt, updatedAt", async () => {
    42→    const task = await makeTask(client, { title: "Brand new task" });
    43→    expect(task.id).toBeTruthy();
    44→    expect(task.title).toBe("Brand new task");
    45→    expect(task.createdAt).toBeTruthy();
    46→    expect(task.updatedAt).toBeTruthy();
    47→  });
    48→
    49→  it("new tasks have empty subTasks and dailyUpdates", async () => {
    50→    const task = await makeTask(client);
    51→    expect(task.subTasks).toEqual([]);
    52→    expect(task.dailyUpdates).toEqual([]);
    53→  });
    54→
    55→  it("retrieves a task by id", async () => {
    56→    const created = await makeTask(client, { title: "Find me" });
    57→    const found = await client.getTask(created.id);
    58→    expect(found?.id).toBe(created.id);
    59→    expect(found?.title).toBe("Find me");
    60→  });
    61→
    62→  it("returns undefined for unknown task id", async () => {
    63→    const result = await client.getTask("nonexistent-id");
    64→    expect(result).toBeUndefined();
    65→  });
    66→
    67→  it("updates a task title without affecting other fields", async () => {
    68→    const created = await makeTask(client, { title: "Original", priority: "High" });
    69→    const updated = await client.updateTask(created.id, { title: "Updated" });
    70→    expect(updated.title).toBe("Updated");
    71→    expect(updated.priority).toBe("High");
    72→    expect(updated.status).toBe("To Do");
    73→  });
    74→
    75→  it("deletes a task so it no longer appears in getTasks", async () => {
    76→    const created = await makeTask(client, { title: "Delete me" });
    77→    await client.deleteTask(created.id);
    78→    const found = await client.getTask(created.id);
    79→    expect(found).toBeUndefined();
    80→  });
    81→
    82→  it("throws when deleting a non-existent task id", async () => {
    83→    await expect(client.deleteTask("missing-task-id")).rejects.toThrow("Task not found");
    84→  });
    85→});
    86→
    87→describe("MockApiClient — Task Validation", () => {
    88→  let client: ApiClient;
    89→
    90→  beforeEach(async () => {
    91→    client = await createClient();
    92→  });
    93→
    94→  it("rejects creating a Blocked task without a blocking reason", async () => {
    95→    await expect(
    96→      makeTask(client, { status: "Blocked", blockingReason: "" }),
    97→    ).rejects.toThrow("Blocking reason is required");
    98→  });
    99→
   100→  it("allows creating a Blocked task with a blocking reason", async () => {
   101→    const task = await makeTask(client, {
   102→      title: "Blocked task",
   103→      status: "Blocked",
   104→      blockingReason: "Waiting for approvals",
   105→    });
   106→    expect(task.status).toBe("Blocked");
   107→    expect(task.blockingReason).toBe("Waiting for approvals");
   108→  });
   109→
